/* Auto-resize for chat page input - allow multi-line with max height */
.input-container.fixed .message-input {
  height: auto !important;
  min-height: 1.5rem !important;
  max-height: 9rem !important; /* 6 lines * 1.5rem line-height */
  resize: none;
  overflow-y: auto;
  transition: height 0.1s ease;
}

/* Welcome page input container - longer than chat */
.welcome-container .input-container {
  width: 100%; /* Full width for welcome page */
  min-width: 700px; /* Ensure minimum width */
  max-width: 1000px;
  margin: 1rem auto 0;
  padding: 0 1rem;
  display: flex;
  justify-content: center;
}

/* Auto-resize for welcome page input - allow multi-line with max height */
.welcome-container .message-input {
  height: auto !important;
  min-height: 1.5rem !important;
  max-height: 9rem !important; /* 6 lines * 1.5rem line-height */
  resize: none;
  overflow-y: auto;
  transition: height 0.1s ease;
}

.input-wrapper {
  position: relative;
  display: flex;
  align-items: center;
  background: var(--input-bg);
  border: 1px solid var(--input-border);
  border-radius: 1.5rem;
  padding: 0.5rem 1rem;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  width: 100%;
  box-sizing: border-box;
}

/* Auto-resize for input wrapper in fixed containers */
.input-container.fixed .input-wrapper {
  min-height: 3.25rem; /* Minimum height */
  max-height: 10.5rem; /* Maximum height to accommodate 6 lines + padding */
  height: auto;
  align-items: flex-end; /* Align items to bottom for better appearance */
}

